import React from 'react';
import {Tr, Td, Avatar, AvatarGroup, Flex, Text, Progress, Icon, useColorModeValue} from "@chakra-ui/react";


function DashboardTableRow(props) {
    const {logo, name, members, budget, progression} = props;
    const textColor = useColorModeValue("gray.700", "white");
    return (
        <Tr>
            <Td minWidth={{sm: "250px"}} pl="0px">
                <Flex align="center" py=".8rem" minWidth="100%" flexWrap="nowrap">
                    <Icon as={logo} h={"24px"} w={"24px"} pr="5px"/>
                    <Text fontSize="md" color={textColor} fontWeight="bold" minWidth="100%">{name}</Text>
                </Flex>
            </Td>

            <Td>
                <AvatarGroup size="sm">
                    {
                        members.map(member => {
                            return (
                                <Avatar name="Ryan Florence" src={member} _hover={{zIndex: '3', cursor: 'pointer'}}/>
                            )
                        })
                    }
                </AvatarGroup>
            </Td>
            <Td>
                <Text fontSize="md" color={textColor} fontWeight="bold" pb=".5rem">{budget}</Text>
            </Td>
            <Td>
                <Flex direction="column">
                <Text fontSize="md" color="teal.300" fontWeight="bold" pb=".2rem">{`${progression}%`}</Text>
                <Progress colorScheme={progression === 100 ? "teal" : "cyan"} size="xs" value={progression} borderRadius="15px"/>
                </Flex>
            </Td>
        </Tr>
    )
}

export default DashboardTableRow
